function incrementarContador() {
    let count = localStorage.getItem('pdfCount') || 0;
    count = parseInt(count) + 1;
    localStorage.setItem('pdfCount', count);
    return count;
}

function obtenerContador() {
    return parseInt(localStorage.getItem('pdfCount')) || 0;
}

const generarPDF = () => {
    const cliente = document.getElementById('cliente').value.trim();
    const productos = CotizacionApp.productos; // Accessing productos from CotizacionApp
    const total = CotizacionApp.total; // Accessing total from CotizacionApp

    if (productos.length === 0) {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: 'No se puede generar un PDF vacío. Agrega al menos un producto.',
        });
        return;
    }

    if (!cliente) {
        Swal.fire({
            icon: 'error',
            title: 'Error',
            text: 'Por favor, ingresa el nombre del cliente.',
        });
        return;
    }

    const cotizacionNumero = incrementarContador();
    const cotizacionTexto = `Cotización #${cotizacionNumero.toString().padStart(5, '0')}`;
    const fecha = dayjs().format('DD/MM/YYYY HH:mm:ss');
    const logoBase64 = 'data:image/png;base64,';

    const docDefinition = {
        content: [
            {
                columns: [
                    {
                        image: logoBase64,
                        width: 150
                    },
                    {
                        text: [
                            { text: cotizacionTexto + '\n', style: 'header' },
                            { text: `Cliente: ${cliente}\n` },
                            { text: `Fecha: ${fecha}\n` }
                        ],
                        alignment: 'right'
                    }
                ]
            },
            {
                style: 'tableExample',
                table: {
                    headerRows: 1,
                    widths: ['*', 'auto', 'auto', 'auto'],
                    body: [
                        [{ text: 'Producto', style: 'tableHeader' }, { text: 'Cantidad', style: 'tableHeader' }, { text: 'Precio Unitario', style: 'tableHeader' }, { text: 'Subtotal', style: 'tableHeader' }],
                        ...productos.map(p => [p.nombre, p.cantidad.toString(), `Q${p.precio.toFixed(2)} GTQ`, `Q${p.cantidad.times(p.precio).toFixed(2)} GTQ`]),
                        [{ text: 'Total', colSpan: 3, alignment: 'right', style: 'tableHeader' }, {}, {}, `Q${total.toFixed(2)} GTQ`]
                    ]
                },
                layout: 'lightHorizontalLines'
            }
        ],
        styles: {
            header: {
                fontSize: 18,
                bold: true,
                margin: [0, 0, 0, 10]
            },
            tableExample: {
                margin: [0, 5, 0, 15]
            },
            tableHeader: {
                bold: true,
                fontSize: 13,
                color: 'black'
            }
        }
    };

    const fechaHora = dayjs().format('YYYYMMDD_HHmmss');
    pdfMake.createPdf(docDefinition).download(`cotizacion_${cotizacionNumero.toString().padStart(5, '0')}_${fechaHora}.pdf`);
    
    console.log(`PDFs generados: ${cotizacionNumero}`);
};

// Inicialización del contador y mostrar en consola
document.addEventListener('DOMContentLoaded', () => {
    const count = obtenerContador();
    console.log(`PDFs generados anteriormente: ${count}`);
});